import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import {OwnableUpgradeable} from "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import {Initializable} from "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import {ERC20Upgradeable} from "@openzeppelin/contracts-upgradeable/token/ERC20/ERC20Upgradeable.sol";
import {StringsUpgradeable} from "@openzeppelin/contracts-upgradeable/utils/StringsUpgradeable.sol";

import {AstralAssetVault} from "./AstralAssetValut.sol";
//right now transfer and transfer from can be called by anyone
//in futrue disable thi so that both can only be called by the assets gateway contract

//to do set up UUPS pattern and acces coontrol with Roles

///AstralRC20 represents a digital asset that has been bridged on to
/// the Ethereum ledger. It exposes mint and burn functions that can only be
/// called by it's associated Gateway contract.
contract AstralERC20Logic is Initializable, OwnableUpgradeable, ERC20Upgradeable {
    using SafeMath for uint256;

    uint8 internal setDecimals;
    uint256 public constant _rateScale = 1e18;
    uint256 internal _rate;
    uint256 public chainId;
    address parentAsset;
    AstralAssetVault tokenVault;

    event LogRateChanged(uint256 indexed _rate);

    function initialize(
        string memory _name, 
        string memory _symbol, 
        uint8 _decimals, 
        uint256 _chainId,
        uint256 rate,
        address _parentAsset
    ) external initializer {
         //do more checks here to make sure source asset is legit
        //address checks
         __Ownable_init();
        __ERC20_init(_name, _symbol);
    
        require(rate > 0, "rate must be greater than 0");

        chainId = _chainId;
        parentAsset = _parentAsset;
        _rate = rate;
        if(_decimals != 0) setDecimals = _decimals;
        else setDecimals = 18;

        // tokenVault = new AstralAssetVault();
    }

    function chain() public view returns (uint256) {
        return chainId;
    }

    function getTokenValut() public view returns (AstralAssetVault) {
        return tokenVault;
    }

    function decimals() public view override returns (uint8) {
        return setDecimals;
    }
    /// @notice mint can only be called by the tokens' associated Gateway
    /// contract. See Gateway's mint function instead.
    function mint(address _to, uint256 _amount) public onlyOwner {
        _mint(_to, _amount);
    }

    /// @notice burn can only be called by the tokens' associated Gateway
    /// contract. See Gateway's burn functions instead.
    function burn(address _from, uint256 _amount) public onlyOwner {
        _burn(_from, _amount);
    }

    function transfer(address recipient, uint256 amount) public override returns (bool) {
        // Disallow sending tokens to the ERC20 contract address - a common
        // mistake caused by the Ethereum transaction's `to` needing to be
        // the token's address.
        require(
            recipient != address(this),
            "RenERC20: can't transfer to token address"
        );
        return super.transfer(recipient, amount);
    }

    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) public override returns (bool) {
        // Disallow sending tokens to the ERC20 contract address (see comment
        // in `transfer`).
        require(
            recipient != address(this),
            "AstralERC20: can't transfer to token address"
        );
        return super.transferFrom(sender, recipient, amount);
    }

    /////////////RATE FUNCTIONS FOR BRIDGE ASSET FEE/////////////////////

        function setExchangeRate(uint256 _nextRate) public onlyOwner {
        _setRate(_nextRate);
    }

    function exchangeRateCurrent() public view returns (uint256) {
        require(_rate != 0, "ERC20WithRate: rate has not been initialized");
        return _rate;
    }

    function _setRate(uint256 _nextRate) internal {
        require(_nextRate > 0, "ERC20WithRate: rate must be greater than zero");
        _rate = _nextRate;
    }

    function balanceOfUnderlying(address _account)
        public
        view
        returns (uint256)
    {
        return toUnderlying(balanceOf(_account));
    }

    function toUnderlying(uint256 _amount) public view returns (uint256) {
        return _amount.mul(_rate).div(_rateScale);
    }

    function fromUnderlying(uint256 _amountUnderlying)
        public
        view
        returns (uint256)
    {
        return _amountUnderlying.mul(_rateScale).div(_rate);
    }

}","keccak256":"0x54674a4a4677705ae8eaf5294d9402c32bfc4faf9679b7c74be27c0b6d619dc5"},"contracts/AstralABridge/BridgeBaseAdapter.sol":{"content":"pragma solidity >=0.8.9;
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {ContextUpgradeable} from "@openzeppelin/contracts-upgradeable/utils/ContextUpgradeable.sol";
import {Initializable} from "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import {AstralERC20Logic} from "./AstralERC20Asset/AstralERC20.sol";
import {TestNativeAssetRegistry} from "./tesNativeAssetRegistry.sol";
import {ECDSA} from "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import {AstralAssetVault} from "./AstralERC20Asset/AstralAssetValut.sol";

import "hardhat/console.sol";

interface IToken {
    function mint(address to, uint256 amount) external;

    function burn(address owner, uint256 amount) external;
}

contract BridgeBase is Initializable, ContextUpgradeable {
	
    address public admin;
    AstralERC20Logic public token;
    uint256 mintFee; //for now
    uint256 burnFee; //fornow
    uint256 lockMintNonce;
    uint256 burnReleaseNonce;

    mapping(uint256 => bool) public processedBurnNonces;
    mapping(uint256 => bool) public processedLockNonces;

    mapping(address => uint256) lockBalance;

    enum Step {
        Burn,
        Mint
    }

    event MintEvent(
        address from,
        address to,
        uint256 amount,
        uint256 date,
        uint256 nonce,
        Step indexed step
    );

    event ReleaseEvent(
        address from,
        address to,
        uint256 amount,
        uint256 date,
        uint256 nonce,
        Step indexed step
    );

    event AssetLocked(
        address _for,
        uint256 amount,
        uint256 timestamp,
        uint256 nonce
    );
    event AssetBurnt(
        address _for,
        uint256 amount,
        uint256 timestamp,
        uint256 nonce
    );

    function initialize(address _admin, address _token) external initializer {
		__Context_init();
        admin = _admin;
        token = AstralERC20Logic(_token);
		mintFee = 0; //for now
		burnFee = 0; //fornow
		lockMintNonce = 0;
		burnReleaseNonce = 0;
    }

    function bridge(
        bytes32 _payloadHash,
        bytes32 _nonceHash,
        bytes memory _sig,
        address to,
        uint256 amount,
        uint256 otherChainNonce,
        string memory bridgeAction,
        address token
    ) external {
        bytes32 BridgeAction = keccak256(bytes(bridgeAction));
        bytes32 mint = keccak256(bytes("MINT"));
        bytes32 burn = keccak256(bytes("BURN"));
        bytes32 lock = keccak256(bytes("LOCK"));
        bytes32 release = keccak256(bytes("RELEASE"));

        bool doesAssetExist = false;
        address[2] memory supportedAstralAssets = [address(this), _msgSender()];
        for (uint256 i = 0; i < supportedAstralAssets.length; i++) {
            if (supportedAstralAssets[i] == token) doesAssetExist = true;
            break;
        }
        if (doesAssetExist && BridgeAction == lock) {
            // AstralERC20Logic(token).transferFrom(to, transferContract, amount);
            // lock();
        } else if (doesAssetExist && BridgeAction == release) {
            // transferContract.transferBackToBridge(address(this), amount);
            // release();
        } else if (!doesAssetExist && BridgeAction == mint) {
            //  mint(
            //   bytes32 _payloadHash,
            //   bytes32 _nonceHash,
            //   bytes memory _sig,
            //   address to,
            //   uint amount,
            //   uint otherChainNonce
            //  );
        } else if (!doesAssetExist && BridgeAction == burn) {
            //  burn(to, amount);
        }
    }

    function burn(address to, uint256 _amount) external {
        require(to != address(0), "invalid burn address");
        require(token.balanceOf(_msgSender()) >= _amount, "insufficent balance");

        uint256 amountFeeRate = token.exchangeRateCurrent();
        uint256 recipientAmount = _amount - amountFeeRate;

        require(
            (_amount * 10000) / 10000 == _amount && _amount > 0,
            "lock amount must be greater than zero"
        );

        //mint for user
        token.burn(_msgSender(), _amount);
        //mint fee for admin
        token.mint(admin, amountFeeRate);

        emit AssetBurnt(
            _msgSender(),
            _amount - amountFeeRate,
            block.timestamp,
            burnReleaseNonce
        );
        burnReleaseNonce += 1;
    }

    function mint(
        bytes32 _payloadHash,
        bytes32 _nonceHash,
        bytes memory _sig,
        uint256 _amount,
        uint256 otherChainNonce,
        address mintAddress
    ) external {
        require(
            processedLockNonces[otherChainNonce] == false,
            "transfer already processed"
        );
        processedLockNonces[otherChainNonce] = true;

        //try do abi.decode payload hash

        bytes32 sigHash = hashForSignature(
            _payloadHash,
            _amount,
            mintAddress,
            _nonceHash
        );
        uint256 tokenFeeRate = token.exchangeRateCurrent();

        require(
            verifySignature(sigHash, _sig),
            "unauthorized mint: signature does not match request"
        );
        require((_amount * 10000) / 10000 == _amount, "amount too low");

        //get the fee for the admin

        uint256 tokenFee = (_amount * tokenFeeRate) / 10000;
        console.log("token feeeeeeee", tokenFee);
        //mint for user
        token.mint(mintAddress, _amount - tokenFee);
        //mint fee for admin
        token.mint(admin, tokenFee);

        emit MintEvent(
            address(this),
            _msgSender(),
            _amount,
            block.timestamp,
            otherChainNonce,
            Step.Mint
        );
    }

    function lock(
        address regsitryAddress,
        address lockAsset,
        uint256 _amount
    ) external {
        TestNativeAssetRegistry registry = TestNativeAssetRegistry(
            regsitryAddress
        );

        address[] memory assetRegistry = registry.getAllNaitveERC20Asset();
        bool doesAssetExist = false;
        for (uint256 i = 0; i < assetRegistry.length; i++) {
            if (assetRegistry[i] == lockAsset) doesAssetExist = true;
            break;
        }
        require(doesAssetExist, "lock asset not supported");
        require(_amount > 0, "lock amount must be greater than zero");
        uint256 amountFeeRate = token.exchangeRateCurrent();

        IERC20(lockAsset).transferFrom(
            _msgSender(),
            address(this),
            _amount - amountFeeRate
        );
        lockBalance[_msgSender()] += _amount - amountFeeRate;

        emit AssetLocked(
            _msgSender(),
            _amount - amountFeeRate,
            block.timestamp,
            lockMintNonce
        );
        lockMintNonce += 1;
    }

    //on the creation of an astralAsset tie in th address of the associated token
    //so we can always know were releasing teh correct token
    function release(
        bytes32 _payloadHash,
        bytes32 _nonceHash,
        bytes memory _sig,
        uint256 _amount,
        address releaseAsset,
        address recipient,
        uint256 otherChainNonce,
        address regsitryAddress
    ) external {
        require(
            processedBurnNonces[otherChainNonce] == false,
            "transfer already processed"
        );
        processedBurnNonces[otherChainNonce] = true;

        TestNativeAssetRegistry registry = TestNativeAssetRegistry(
            regsitryAddress
        );

        address[] memory assetRegistry = registry.getAllNaitveERC20Asset();
        bool doesAssetExist = false;
        for (uint256 i = 0; i < assetRegistry.length; i++) {
            if (assetRegistry[i] == releaseAsset) doesAssetExist = true;
            break;
        }
        require(doesAssetExist, "lock asset not supported");

        bytes32 sigHash = hashForSignature(
            _payloadHash,
            _amount,
            recipient,
            _nonceHash
        );
        uint256 tokenFeeRate = token.exchangeRateCurrent();

        uint256 tokenFee = (_amount * tokenFeeRate) / 10000;
        require(
            verifySignature(sigHash, _sig),
            "unauthorized mint: signature does not match request"
        );
        console.log(
            "lock balance is ",
            lockBalance[recipient],
            tokenFee,
            _amount
        );
        require(
            (_amount * 10000) / 10000 == _amount &&
                _amount - tokenFee <= lockBalance[recipient],
            "amount too low"
        );

        //mint for user
        IERC20(releaseAsset).transfer(recipient, _amount - tokenFee);
        IERC20(releaseAsset).transfer(admin, tokenFee);

        emit ReleaseEvent(
            recipient,
            address(this),
            _amount,
            block.timestamp,
            burnReleaseNonce,
            Step.Burn
        );
    }

    function hashForSignature(
        bytes32 _pHash,
        uint256 _amount,
        address _to,
        bytes32 _nHash
    ) public view returns (bytes32) {
        return
            keccak256(abi.encode(_pHash, _amount, address(token), _to, _nHash));
    }

    function verifySignature(bytes32 _sigHash, bytes memory _sig)
        public
        view
        returns (bool)
    {
        console.log("admin address", admin);
        return admin == ECDSA.recover(_sigHash, _sig);
    }

    function getBalance(address user) public view returns (uint256) {
        return lockBalance[user];
    }

    function checkNonce(uint256 _nonce, string memory _type)
        external
        view
        returns (bool)
    {
        if (keccak256(bytes(_type)) == keccak256(bytes("Burn"))) {
            return processedBurnNonces[_nonce];
        } else return processedLockNonces[_nonce];
    }

    //have registry address as global var
    function doesReleaseAssetExist(
        address regsitryAddress,
        address _releaseAsset
    ) external view returns (bool) {
        TestNativeAssetRegistry registry = TestNativeAssetRegistry(
            regsitryAddress
        );

        address[] memory assetRegistry = registry.getAllNaitveERC20Asset();
        bool doesAssetExist = false;
        for (uint256 i = 0; i < assetRegistry.length; i++) {
            if (assetRegistry[i] == _releaseAsset) doesAssetExist = true;
            break;
        }
        return doesAssetExist;
    }
}
